settings.custom_liquid:
{% if product %}
    <section class="sticky-product">
        <div class="container sticky-product__container">
            <div class="sticky-product__column column">
                {% if product.images.size > 0 %}
                    <div class="sticky-product__image">
                        <img src="{{ product.images[0].src | img_url }}" alt="{{ product.title }}" />
                    </div>
                {% endif %}
            </div>
            <div class="sticky-product__column column">
                <h3 class="sticky-product__title">{{ product.title }}</h3>
            </div>
            <div class="sticky-product__column column">
                {% if product.price %}
                    <span class="product-price">{{ product.price | money }}</span>
                {% else %}
                    {% assign priceBlock = sections['sticky-section'].blocks.price %}
                    {% if priceBlock %}
                        <span class="product-price">{{ priceBlock.settings.template_price }}</span>
                    {% endif %}
                {% endif %}
            </div>
            <div class="sticky-product__column column">
                <form action="/cart/add" method="post">
                    <input type="hidden" name="id" value="{{ product.variants.first.id }}" />
                    <button type="submit" class="btn">Add to Cart</button>
                </form>
            </div>
        </div>
    </section>
{% endif %}
